// requering npm
const express = require('express');
const app = express();
const hbs = require('hbs');
const bodyParser = require('body-parser');

app.use(bodyParser.urlencoded({extended:true}));

app.use(express.static('public'));

app.set('views', __dirname + '/views');
app.set('view engine', 'hbs');

//array visible
let campgrounds = [
    {name: "Salmon Creek", image:"data:image/jpeg;base64,"},

    {name: "Granite Hill", image:"data:image/jpeg;base64,"},

    {name: "Mount Goat's Rest" , image:"data:image/jpeg;base64,"}
]

//Routes
app.get('/', (req,res,next)=>{
    res.render('landing')
})

app.get('/campgrounds', (req,res,next)=>{
    //it shows the campgrounds name and images
    res.render("campgrounds", {campgrounds:campgrounds}); 
})

// to add a new campground has to be in the same url that we want to show it(action form)
app.post('/campgrounds',(req,res,next)=>{
    //to get data from form and add data to campgrounds array
   let name = req.body.name;
   let image = req.body.image;
   let newCampground = {name:name, image:image}
   campgrounds.push(newCampground);
    //redirect back to campgrounds page
    res.redirect('/campgrounds')
})


app.get('/campgrounds/new', (req,res,next)=>{
    //here is where the form is, which submit a post request to campgrounds
    res.render('new')
})




// Server
const port = process.env.port || 3000;
app.listen(port, (req,res,next)=>{
    console.log("Server is Running!!!")
})